    protected void reset(){
        textid.setText(null);
        textkode.setText(null);
        textnama.setText(null);
        textjumlah.setText(null);
        textid.requestFocus();
    }

    public void noTable(){
        int Baris = tm.getRowCount();
        for(int a= 0; a< Baris; a++){
            String nomor = String.valueOf(a+1);
            tm.setValueAt(nomor +".", a, 0);
        }
    }
    public void lebarKolom(){
        TableColumn kolom;
        tblbarang.setAutoResizeMode(javax.swing.JTable.AUTO_RESIZE_ALL_COLUMNS);
        kolom = tblbarang.getColumnModel().getColumn(0);
        kolom.setPreferredWidth(40);
        kolom = tblbarang.getColumnModel().getColumn(1);
        kolom.setPreferredWidth(50);
        kolom = tblbarang.getColumnModel().getColumn(2);
        kolom.setPreferredWidth(100);
        kolom = tblbarang.getColumnModel().getColumn(3);
        kolom.setPreferredWidth(100);
        kolom = tblbarang.getColumnModel().getColumn(4);
        kolom.setPreferredWidth(100);
        
    }
    
    public void dataTable(){
        Object[] baris = {"No","ID","Kode","Nama Barang","Jumlah",};
        tm = new DefaultTableModel(null, baris);
        tblbarang.setModel(tm);
        String sql = "select * from tbl_barang order by id_barang asc";
        try {
            java.sql.Statement stat = conn.createStatement();
            ResultSet hasil = stat.executeQuery(sql);
            while (hasil.next()){
                String id_barang = hasil.getString("id_barang");
                String kode_barang = hasil.getString("kode_barang");
                String nama_barang = hasil.getString("nama_barang");
                String jumlah_barang = hasil.getString("jumlah_barang");
                String [] data = {"",id_barang,kode_barang,nama_barang,jumlah_barang};
                tm.addRow(data);
                noTable();
                lebarKolom();
                }
            }catch (SQLException e){
                JOptionPane.showMessageDialog(null, "Data Tidak Bisa ditampikan");
        }
        
    }
    
    
    public Databarang(java.awt.Frame parent, boolean modal) {
        super(parent, modal);
        initComponents();
        dataTable();
        lebarKolom();
    }

