    private void btnsimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnsimpanActionPerformed
        if (textid.getText().equals("")){
        JOptionPane.showConfirmDialog(null, "Kolom Id Barang Silahkan Diisi");
        } else if (textkode.getText().equals("")){
            JOptionPane.showConfirmDialog(null, "Kolom Kode Barang Silahkan Diisi");
        } else if (textnama.getText().equals("")){
            JOptionPane.showConfirmDialog(null, "Kolom Nama Barang Silahkan Diisi");
        } else {
            String sql = "insert into tbl_barang (id_barang,kode_barang,nama_barang,jumlah_barang) values (?,?,?,?)";
            try {
                PreparedStatement stat = conn.prepareStatement(sql);
                JOptionPane.showMessageDialog(null,"Data Berhasil Disimpan");
                stat.setString(1, textid.getText());
                stat.setString(2, textkode.getText());
                stat.setString(3, textnama.getText());
                stat.setString(4, textjumlah.getText());
                stat.executeUpdate();
                dataTable();
                textid.requestFocus();
            } catch(SQLException e){
                JOptionPane.showMessageDialog(null, "Penyimpanan Data Gagal"+ e);
            }
        }
    }//GEN-LAST:event_btnsimpanActionPerformed

    private void textidKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_textidKeyPressed
       if(evt.getKeyCode() == KeyEvent.VK_ENTER){
           textkode.requestFocus();
       }
    }//GEN-LAST:event_textidKeyPressed

    private void textkodeKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_textkodeKeyPressed
        if(evt.getKeyCode() == KeyEvent.VK_ENTER){
           textnama.requestFocus();
        }
    }//GEN-LAST:event_textkodeKeyPressed

    private void btnhapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnhapusActionPerformed
        int ok = JOptionPane.showConfirmDialog(null,"Apakah Anda Yakin Ingin Menghapus Data Ini","Kondirmasi", JOptionPane.YES_NO_OPTION);
        if (ok == 0){
            String sql = "delete from tbl_barang where id_barang='"+ textid.getText() +"'";
            try{
                PreparedStatement stat = conn.prepareStatement(sql);
                stat.executeUpdate(); 
                JOptionPane.showMessageDialog(null,"Data Berhasil Dihapus");
                dataTable();
                reset();
                textid.requestFocus();
            } catch (SQLException e){
                JOptionPane.showMessageDialog(null,"Data Gagal Dihapus" +e);
            }
        }
    }//GEN-LAST:event_btnhapusActionPerformed

